    private static List<Integer> algorithm2(String[] arr, String v) {

        int capacity = arr[0].length() * 2;

        StringBuilder sb = new StringBuilder(capacity);
        Predicate<String> isPermutationPredicate = (sbLocal) -> isPermutation(arr, sbLocal, arr[0].length());

        return doSlidingWindow(arr, v, sb, isPermutationPredicate);
    }

    private static boolean isPermutation(String[] arr, String text, int size) {
        List<String> strings = splitEqually(text, size);
        boolean[] chosen = new boolean[arr.length];

        int seeCount = 0;
        for (String s : strings) {

            int oldCount = seeCount;

            for (int i = 0; i < arr.length; i++) {
                if (chosen[i]) continue;
                if (arr[i].equals(s)) {
                    chosen[i] = true;
                    seeCount++;
                    break;
                }
            }

            if (oldCount == seeCount) return false;
        }
        return seeCount == arr.length;
    }

    private static List<String> splitEqually(String text, int size) {

        List<String> result = new ArrayList<>(text.length());

        for (int i = 0; i < text.length(); i = i + size) {
            result.add(text.substring(i, Math.min(text.length(), i + size)));
        }

        return result;
    }

    //Brute force: Memory inefficient
    private static List<Integer> algorithm(String[] arr, String v) {

        int capacity = arr[0].length() * 2;

        StringBuilder sb = new StringBuilder(capacity);
        boolean[] chosen = new boolean[arr.length];
        List<String> permutations = new ArrayList<>(capacity);

        solve(arr, permutations, sb, chosen);
//        out.println(permutations);
        Predicate<String> predicate = permutations::contains;

        return doSlidingWindow(arr, v, sb, predicate);
    }

    private static List<Integer> doSlidingWindow(String[] arr, String v, StringBuilder sb, Predicate<String> predicate) {
        List<Integer> result = new ArrayList<>();
        Map<String, Boolean> wordRemember = new HashMap<>();

        int windowSize = (arr[0].length() * arr.length);
        int leftPointer = 0;
        int rightPointer = 0;

        //Init window [0, 6, 3]
        while (rightPointer < windowSize && rightPointer < v.length()) {
            sb.append(v.charAt(rightPointer));
            rightPointer++;
        }

        String subString = sb.toString();
        boolean testResult = predicate.test(subString);
        wordRemember.put(subString, testResult);
        if (testResult) {
            result.add(leftPointer);
        }

        while (rightPointer < v.length()) {
            sb.deleteCharAt(0);
            leftPointer++;
            sb.append(v.charAt(rightPointer++));
            String newSubString = sb.toString();

            boolean getCachedResultIfWordHasBeenElseCompute;

            if (wordRemember.get(newSubString) == null) {
                getCachedResultIfWordHasBeenElseCompute = predicate.test(newSubString);
                wordRemember.put(newSubString, getCachedResultIfWordHasBeenElseCompute);
            } else {
                getCachedResultIfWordHasBeenElseCompute = wordRemember.get(newSubString);
            }

            if (getCachedResultIfWordHasBeenElseCompute && !result.contains(leftPointer)) {
                result.add(leftPointer);
            }
        }

        return result;
    }

    private static void solve(String[] arr, List<String> permutations, StringBuilder sb, boolean[] chosen) {
        if (sb.length() == (arr[0].length() * arr.length)) {
            permutations.add(sb.toString());
        } else {
            for (int i = 0; i < arr.length; i++) {

                if (chosen[i]) continue;
                chosen[i] = true;
                String str = arr[i];
                sb.append(str);
                solve(arr, permutations, sb, chosen);
                chosen[i] = false;
                int strL = str.length();
                sb.delete((sb.length() - strL), sb.length());
            }
        }
    }

